import React, { Component, useEffect, useState } from 'react';
import Slider from 'react-slick';

import { connect } from 'react-redux';
import { getProductsByCollectionHelper } from '~/utilities/strapi-fetch-data-helpers';
import Product from '~/components/elements/products/Product';
import { carouselFullwidth, carouselStandard } from '~/utilities/carousel-helpers';
import NextArrow from '~/components/elements/carousel/NextArrow';
import PrevArrow from '~/components/elements/carousel/PrevArrow';

const RelatedProduct = ({ collectionSlug, boxed, layout }) => {
    const [productItems, setProductItems] = useState(null);
    const [loading, setLoading] = useState(true);

    async function getProducts() {
        setLoading(true);
        const responseData = await getProductsByCollectionHelper(
            collectionSlug
        );
        if (responseData) {
            setProductItems(responseData.items);
            setTimeout(
                function () {
                    setLoading(false);
                }.bind(this),
                250
            );
        }
    }

    useEffect(() => {
        getProducts();
    }, [collectionSlug]);

    // const carouselFullwidth = {
    //     dots: false,
    //     infinite: productItems && productItems.length > 7 ? true : false,
    //     speed: 750,
    //     slidesToShow: 7,
    //     slidesToScroll: 3,
    //     arrows: true,
    //     nextArrow: <NextArrow />,
    //     prevArrow: <PrevArrow />,
    //     lazyload: true,
    //     responsive: [
    //         {
    //             breakpoint: 1750,
    //             settings: {
    //                 slidesToShow: 6,
    //                 slidesToScroll: 3,
    //                 dots: true,
    //                 arrows: false,
    //             },
    //         },
    //
    //         {
    //             breakpoint: 1366,
    //             settings: {
    //                 slidesToShow: 5,
    //                 slidesToScroll: 2,
    //                 infinite: true,
    //                 dots: true,
    //                 arrows: false,
    //             },
    //         },
    //         {
    //             breakpoint: 1200,
    //             settings: {
    //                 slidesToShow: 4,
    //                 slidesToScroll: 1,
    //                 infinite: true,
    //                 dots: true,
    //             },
    //         },
    //         {
    //             breakpoint: 1024,
    //             settings: {
    //                 slidesToShow: 4,
    //                 slidesToScroll: 1,
    //                 infinite: true,
    //                 dots: true,
    //             },
    //         },
    //         {
    //             breakpoint: 768,
    //             settings: {
    //                 slidesToShow: 3,
    //                 slidesToScroll: 2,
    //                 dots: true,
    //                 arrows: false,
    //             },
    //         },
    //         {
    //             breakpoint: 480,
    //             settings: {
    //                 slidesToShow: 2,
    //                 dots: true,
    //                 arrows: false,
    //             },
    //         },
    //     ],
    // };

    // Views
    let carouselView;
    if (!loading) {
        if (productItems) {
            if ((layout = 'fullwidth')) {
                carouselView = (
                    <Slider
                        {...carouselFullwidth}
                        className="ps-carousel outside">
                        {productItems.map((item, index) => {
                            if (index < 8) {
                                return <Product product={item} key={item.id} />;
                            }
                        })}
                    </Slider>
                );
            } else {
                carouselView = (
                    <Slider
                        {...carouselStandard}
                        className="ps-carousel outside">
                        {productItems.map((item, index) => {
                            if (index < 8) {
                                return <Product product={item} key={item.id} />;
                            }
                        })}
                    </Slider>
                );
            }
        } else {
            carouselView = <p>No product found.</p>;
        }
    } else {
        carouselView = <p>Loading...</p>;
    }

    return (
        <div
            className={`ps-section--default ps-related-products`}>
            <div className="ps-section__header">
                <h3>Relevant</h3>
            </div>
            <div className="ps-section__content">{carouselView}</div>
        </div>
    );
};

export default connect((state) => state.collection)(RelatedProduct);
